mapper.select:
SELECT
*
FROM t_stocktake_item



        AND t_stocktake_item.stocktake_id = #{queryForm.stocktakeId}



        AND t_stocktake_item.sku_id = #{queryForm.skuId}



        AND t_stocktake_item.goods_id = #{queryForm.goodsId}



        AND t_stocktake_item.category_id = #{queryForm.categoryId}
